import React, { Component } from 'react'
import axios from 'axios'

import { Button, Navbar, Nav, NavLink, NavDropdown, Form, FormControl } from 'react-bootstrap'
import InputGroup from 'react-bootstrap/InputGroup'
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

import Suggestions from './Suggestions'

const { API_KEY } = process.env
const API_URL = 'http://api.musicgraph.com/api/v2/artist/suggest'

class Search extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
         name: ' ',
         email: ' '
      };
   }

   handleChange = (e) => {
      this.setState({
         [e.target.name]: e.target.value
      })
   }

   onSubmit = (e) => {
      e.preventDefault();
      const form = {
         name: this.state.name,
         email: this.state.email
      }
      {/* -----------you would send data to API to get results, I used database for ease, this also clears the form on submit----------------*/ }
      this.props.searchQueryProp = form;
      this.setState({
         name: '',
         email: ''
      })
   }

   render() {
      return (
         <div>
            <form>
               <label>
                  Name:
                    <input
                     name='name'
                     value={this.state.name}
                     onChange={e => this.handleChange(e)} />
               </label>
               <label>
                  Email:
                    <input
                     name='email'
                     value={this.state.email}
                     onChange={e => this.handleChange(e)} />
               </label>
               <button onClick={(e) => this.onSubmit(e)}>Send</button>
            </form>
         </div>
      );
   }
}

export default Search;
