import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import 'bootstrap/dist/css/bootstrap.min.css';

//Page Core
import { Router, Route, Link } from 'react-router'
//import NavBarTop from './component/NavBarTop';

//Navbar Core
//React-BS Button, Navbar and Switch Material-UI
import { Button, Navbar, Nav, NavLink, NavDropdown, Form, FormControl } from 'react-bootstrap'
import BookmarkBorderOutlinedIcon from '@material-ui/icons/BookmarkBorderOutlined';
import Switch from '@material-ui/core/Switch';
import FormGroup from '@material-ui/core/FormGroup';
import FormControlLabel from '@material-ui/core/FormControlLabel';
import { withStyles } from '@material-ui/core/styles';
import InputGroup from 'react-bootstrap/InputGroup'
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

//Cards
import styled from 'styled-components'

//Axios for Guardian and NYT
import HomePageGuardian from './component/HomePageGuardian';

const BlueIOSSwitch = withStyles((theme) => ({
    root: {
        width: 52,
        height: 23,
        padding: 0,
        margin: theme.spacing(1),
    },
    switchBase: {
        padding: 1,
        '&$checked': {
            transform: 'translateX(27.5px)',
            color: theme.palette.common.white,
            '& + $track': {
                backgroundColor: '#0693E3',
                opacity: 1,
                border: 'none',
            },
        },
        '&$focusVisible $thumb': {
            color: '#52d869',
            border: '6px solid #fff',
        },
    },
    thumb: {
        width: 22,
        height: 22,
    },
    track: {
        borderRadius: 26 / 2,
        border: `1px solid ${theme.palette.grey[400]}`,
        backgroundColor: theme.palette.grey[50],
        opacity: 1,
        transition: theme.transitions.create(['background-color', 'border']),
    },
    checked: {},
    focusVisible: {},
}))(({ classes, ...props }) => {
    return (
        <Switch
            focusVisibleClassName={classes.focusVisible}
            disableRipple
            classes={{
                root: classes.root,
                switchBase: classes.switchBase,
                thumb: classes.thumb,
                track: classes.track,
                checked: classes.checked,
            }}
            {...props}
        />
    );
});

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            apiResponse: "",
            switch1: true,
        };
    }

    handleSwitchChange = nr => () => {
        let switchNumber = `switch${nr}`;
        this.setState({
            [switchNumber]: !this.state[switchNumber]
        });
    }

    callAPI() {
        fetch("http://localhost:9000/testAPI")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }))
            .catch(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    render() {
        return (
            <div>
                <NavBarTop />
                <HomePageGuardian />
            </div>
        );
    }
}

export default App;
