import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//React Share
import ShareIcon from '@material-ui/icons/Share';
import {
   EmailShareButton,
   FacebookShareButton,
   TwitterShareButton,
   FacebookIcon,
   EmailIcon,
   TwitterIcon
} from "react-share";
import { Modal, Button } from 'react-bootstrap';

//Loader Spinner
import { css } from "@emotion/core";
import BounceLoader from "react-spinners/BounceLoader";

//Expanded Card Div
import ExpandedCard from './ExpandedCard';

class BookmarksPage extends Component {
   constructor(props) {
      super(props);
      this.state = {
         posts: [],
         isShareModalOpen: false,
         shareTitle: '',
         shareURL: '',
         cardExpanded: false,
         cardIdentifierentifier: '',
         expandedCardContent: {},
      };
      this.props = {
         currentPage: '',
      }
      var retrievedObject = localStorage.getItem('bookmarks');
      this.state.bookmarksArray = JSON.parse(retrievedObject);

   }

   truncateDate(dateinput) {
      return dateinput.substring(0, 10);
   }

   categoryHandler(category) {
      return category.toUpperCase();
   }

   categoryCss(category) {
      category = (category || '').replace(/^\s+|\s+$/g, '');
      category = category.toUpperCase();
      if (category === 'POLITICS') {
         return { backgroundColor: '#0893a1', color: '#fff' };
      }
      else if (category === 'WORLD') {
         return { backgroundColor: '#5904f6', color: '#fff' };
      }
      else if (category === 'SPORT') {
         return { backgroundColor: '#fcb900', color: '#000' };
      }
      else if (category === 'BUSINESS') {
         return { backgroundColor: '#1273de', color: '#fff' };
      }
      else if (category === 'TECHNOLOGY') {
         return { backgroundColor: '#8bc34a', color: '#000' };
      }
      else {
         return { backgroundColor: '#9b9b9b', color: '#fff' };
      }
   }

   imageHandler(url) {
      let currentimage = '';
      try {
         currentimage = url.elements[0].assets[1].file;
      } catch (e) {
         currentimage = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
      }
      return currentimage;
   }

   toggleModal = () => {
      this.setState({
         isShareModalOpen: !this.state.isShareModalOpen
      });
   }

   doExpand = id => {
      this.setState({
         cardIdentifier: id,
         cardExpanded: true,
      });
   }

   render() {
      const { error, isLoaded, posts } = this.state;

      return (
         <div>
            {!this.state.cardExpanded &&
               <ul style={{ listStyleType: "none", padding: "0", paddingLeft: '15px' }} className={this.state.cardExpanded ? 'hidden' : ''}>
                  {
                     this.state.bookmarksArray.map(post => (
                        <li style={{ float: 'left', overflow: 'Hidden' }}>
                           <CardDiv>
                              <div>
                                 <div>
                                    <p style={{ fontSize: '12px' }}><i>
                                       <b onClick={e => this.doExpand(post.id)} >{post.datacontent.webTitle}</b>
                                       <ShareIcon style={{ fontSize: '12px' }} onClick={() => {
                                          this.toggleModal();
                                          this.setState({
                                             shareTitle: post.datacontent.datacontent.webTitle,
                                             shareURL: post.datacontent.datacontent.webUrl
                                          });
                                       }} />
                                    </i></p>
                                 </div>
                              </div>
                              <img onClick={() => { this.setState({ cardIdentifier: post.datacontent.id }); this.doExpand(post.datacontent.id); }} class="img-thumbnail" src={this.imageHandler(post.datacontent.blocks.main)} style={{ width: '70px', height: '70px', minWidth: '70px', margin: '10px' }} alt="Image Not Found" />


                              <div>
                                 <div id='wrapper' onClick={e => this.doExpand(post.datacontent.id)} >
                                    <div id="left">{this.truncateDate(post.datacontent.webPublicationDate)}</div>
                                    <div id="right" style={this.categoryCss(post.datacontent.sectionId)}>{this.categoryHandler(post.datacontent.sectionId)}</div>
                                 </div>
                              </div>


                           </CardDiv>

                        </li>
                     ))
                  }
               </ul>
            }

            {
               this.state.cardExpanded &&
               <div>
                  <ExpandedCard className={this.state.cardExpanded ? 'block' : 'hidden'} currentcardId={this.state.cardIdentifier} isPartOfBookmarksProp={this.state.cardExpanded} />
               </div>
            }

            <Modal size="lg" aria-labelledby="example-modal-sizes-title-lg" show={this.state.isShareModalOpen} onHide={this.toggleModal}>
               <Modal.Header closeButton>
                  <Modal.Title id="example-modal-sizes-title-lg" >{this.state.shareTitle}</Modal.Title>
               </Modal.Header>
               <Modal.Body>
                  <div style={{ textAlign: 'center', fontSize: '25px' }}><b>Share via</b><br /></div>
                  <div style={{ textAlign: 'center', display: 'flex', justifyContent: 'space-around' }}>
                     <FacebookShareButton url={this.state.shareURL} hashtag='#CSCI_571_NewsApp' > <FacebookIcon size={64} round={true} /></FacebookShareButton>
                     <TwitterShareButton url={this.state.shareURL} title='#CSCI_571_NewsApp'><TwitterIcon size={64} round={true} /></TwitterShareButton>
                     <EmailShareButton url={this.state.shareURL} subject='#CSCI_571_NewsApp'><EmailIcon size={64} round={true} /></EmailShareButton>
                  </div>
               </Modal.Body>
            </Modal>
         </div >
      );
   }
}


const CardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  height: 300px;
  margin: 1.5rem;
  width: 300px;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
  transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  padding-top: 5px;
  &:hover {
        /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
        box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);
        transform: translateY(-0.5px);
    },
`;

const bounceCss = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;

export default BookmarksPage;
