import _ from 'lodash'
import faker from 'faker'
import React, { Component } from 'react'
import { Search, Form, Grid, Header, Segment, Container, List } from 'semantic-ui-react'


export default class SearchApp extends Component {
   state = { results: ['null'], selectedResult: null };

   handleSearchChange = async (event, { value }) => {
      try {
         const response = await fetch(
            `https://api.cognitive.microsoft.com/bing/v7.0/suggestions?mkt=fr-FR&q=${value}`,
            {
               headers: {
                  "Ocp-Apim-Subscription-Key": "d901d64eeb334505825c20a547b760ba"
               }
            }
         );
         const data = await response.json();
         const resultsRaw = data.suggestionGroups[0].searchSuggestions;
         const results = resultsRaw.map(result => ({ title: result.displayText, url: result.url }));
         this.setState({ results });
      } catch (error) {
         console.error(`Error fetching search ${value}`);
      }
   };

   handleResultSelect = (e, { result }) =>
      this.setState({ selectedResult: result });

   onSubmit = (e) => {
      e.preventDefault();
      {/* -----------you would send data to API to get results, I used database for ease, this also clears the form on submit----------------*/ }
      localStorage.setItem('searchQuery', this.state.name);
      let temp = this.state.name;
      this.setState({
         name: null,
      })
      this.state.name = null;
      this.props.searchQueryProp(temp);

   }

   render() {
      const { value, results } = this.state

      return (
         <Form>
            <Search
               onResultSelect={this.handleResultSelect}
               onSearchChange={_.debounce(this.handleSearchChange, 1000, {
                  leading: true,
               })}
               results={this.state.results}
               {...this.props}
               style={{ overflow: 'visible' }}
               defaultValue='Enter keyword...'
            />
            <button onClick={(e) => this.onSubmit(e)} style={{ display: 'none' }}>.</button>
         </Form>
      )
   }
}

const styleLink = document.createElement("link");
styleLink.rel = "stylesheet";
styleLink.href = "https://cdn.jsdelivr.net/npm/semantic-ui/dist/semantic.min.css";
document.head.appendChild(styleLink);
