import React, { Component } from "react";
import logo from "./logo.svg";
import "./App.css";
import 'bootstrap/dist/css/bootstrap.min.css';

//Page Core
import { Router, Route, Link } from 'react-router'
import NavBarTop from './component/NavBarTop';

//Cards
import styled from 'styled-components'

//Axios for Guardian and NYT
import HomePageGuardian from './component/HomePageGuardian';
const axios = require('axios');

const getGuardianData = (e) => {
    let geturl = "http://localhost:9000/guardianAPI";
    axios.get(geturl)
        .then(function (response) {
            // handle success
            console.log(response.data);
        })
        .catch(function (error) {
            // handle error
            console.log(e)
            //alert("Too many requests. Please wait a few seconds")
        })

}

class App extends Component {
    constructor(props) {
        super(props);
        this.state = {
            apiResponse: "",
            switch1: true,
        };
    }

    handleSwitchChange = nr => () => {
        let switchNumber = `switch${nr}`;
        this.setState({
            [switchNumber]: !this.state[switchNumber]
        });
    }

    callAPI() {
        fetch("http://localhost:9000/testAPI")
            .then(res => res.text())
            .then(res => this.setState({ apiResponse: res }))
            .catch(err => err);
    }

    componentDidMount() {
        this.callAPI();
    }

    render() {
        return (
            <div>
                <NavBarTop />

                <CardDiv>
                    Hello World!
                </CardDiv>
            </div>
        );
    }
}

const CardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  display: flex;
  height: 120px;
  margin: 1rem;
  position: relative;
  width: 98%;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
  transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  padding-top: 5px;
  &:hover {
        /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
        box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);
        transform: translateY(-0.5px);
    },
`;

export default App;
