            source,
            (memo, data, name) => {
               const results = _.filter(data.results, isMatch)
               if (results.length) memo[name] = { name, results } // eslint-disable-line no-param-reassign

               return memo
            },
            {},
         )

         this.setState({
            isLoading: false,
            results: filteredResults,
         })
      }, 300)
   }

   handleSearchChangeOriginal = async (event, { value }) => {
      try {
         const response = await fetch(
            `https://api.cognitive.microsoft.com/bing/v7.0/suggestions?mkt=fr-FR&q=${value}`,
            {
               headers: {
                  "Ocp-Apim-Subscription-Key": "d901d64eeb334505825c20a547b760ba" //d901d64eeb334505825c20a547b760ba
               }
            }
         );
         const data = await response.json();
         const resultsRaw = data.suggestionGroups[0].searchSuggestions;
         const results = resultsRaw.map(result => ({ title: result.displayText, url: result.url }));
         console.log(results);
         this.setState({ results });
      } catch (error) {
         console.error(`Error fetching search ${value}`);
      }
   };

   handleResultSelect = (e, { result }) =>
      this.setState({ selectedResult: result });

   render() {
      return (
         <div className="App">
            <Search
               onSearchChange={_.debounce(this.handleSearchChange, 1000, {
                  leading: true
               })}
               results={this.state.results}
               onResultSelect={this.handleResultSelect}
            />
            <Search
               onResultSelect={this.handleResultSelect}
               onSearchChange={_.debounce(this.handleSearchChange, 1000, {
                  leading: true,
               })}
               results={this.state.results}
               {...this.props}
            />
            <SelectedResult result={this.state.selectedResult} />
         </div>
      );
   }
}

export default SearchApp;
