import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//Sometimes images are not present. Handling that case
class ErrorBoundary extends React.Component {
   constructor(props) {
      super(props);
      this.state = { error: null, errorInfo: null };
   }

   componentDidCatch(error, errorInfo) {
      // Catch errors in any components below and re-render with error message
      this.setState({
         error: error,
         errorInfo: errorInfo
      })
      // You can also log error messages to an error reporting service here
   }

   render() {
      if (this.state.errorInfo) {
         // Error path
         return (
            <div>
               <h2>Something went wrong.</h2>
               <details style={{ whiteSpace: 'pre-wrap' }}>
                  {this.state.error && this.state.error.toString()}
                  <br />
                  {this.state.errorInfo.componentStack}
               </details>
            </div>
         );
      }
      // Normally, just render children
      return this.props.children;
   }
}

class HomePageGuardian extends Component {
   constructor(props) {
      super(props);
      this.state = {
         error: null,
         isLoaded: false,
         posts: []
      };
   }

   componentDidMount() {
      //Fetching from the NodeJS backend
      fetch("http://localhost:9000/guardianAPI")
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  isLoaded: true,
                  posts: result.response.results
               });
               console.log(this.state.posts)
            },

            // Handle error 
            (error) => {
               this.setState({
                  isLoaded: true,
                  error
               })
            },
         )
   }

   render() {
      const { error, isLoaded, posts } = this.state;

      if (error) {
         return <div>Error in loading</div>
      } else if (!isLoaded) {
         return <div>Loading ...</div>
      } else {
         return (
            <div>
               {/* <ol>
                  {
                     posts.map(post => (
                        <li align="start">
                           <div>
                              <p>{post.title}</p>
                              <p>{post.id}</p>
                           </div>
                        </li>
                     ))
                  }
               </ol> */}
               <ul style={{ listStyleType: "none", padding: "0" }}>
                  {
                     posts.map(post => (
                        <li >
                           <CardDiv>
                              try{
                                 <img class="img-thumbnail" src={post.blocks.main.elements[0].assets[0].file} style={{ width: '200px', minWidth: '200px', margin: '10px' }} alt="Responsive image" />
                              } catch (e){
                                 <img class="img-thumbnail" src="assets/imagenotfound.png" style={{ width: '200px', minWidth: '200px', margin: '10px' }} alt="Responsive image" />
                              }
                              <div>
                                 <p style={{ fontSize: '20px' }}><b><i>{post.webTitle}</i></b></p> {/* Title */}
                                 <p style={{ fontSize: '12px' }}>
                                    <Truncate lines={6} ellipsis={<span>... </span>}>
                                       {post.blocks.body[0].bodyTextSummary}
                                    </Truncate>
                                 </p>
                                 <div>
                                    <p>{post.webPublicationDate}</p>
                                    <p>{post.sectionId}</p>
                                 </div>
                              </div>
                           </CardDiv>
                        </li>
                     ))
                  }
               </ul>
            </div>
         );
      }
   }
}

const CardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  display: flex;
  height: 220px;
  margin: 1.5rem;
  position: relative;
  width: 98%;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
  transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  padding-top: 5px;
  &:hover {
        /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
        box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);
        transform: translateY(-0.5px);
    },
`;

export default HomePageGuardian;
