import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//React Share
import ShareIcon from '@material-ui/icons/Share';
import {
   EmailShareButton,
   FacebookShareButton,
   TwitterShareButton,
   FacebookIcon,
   EmailIcon,
   TwitterIcon
} from "react-share";
import { Modal, Button } from 'react-bootstrap';

//Loader Spinner
import { css } from "@emotion/core";
import BounceLoader from "react-spinners/BounceLoader";

//Expanded Card Div
import ExpandedCard from './ExpandedCard';

class SearchPageTest extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
         posts: [],
         isShareModalOpen: false,
         shareTitle: '',
         shareOrigin: '',
         shareURL: '',
         cardExpanded: false,
         cardIdentifierentifier: '',
         expandedCardContent: {},
      };
      this.props = {
         currentPage: '',
         currentOrigin: '',
      }

   }

   componentDidMount() {
      //Setting Search to true
      //this.props.searchTrueHandler();

      //Fetching from the NodeJS backend
      let requiredURL = '';
      requiredURL = "http://localhost:9000/guardianAPISearch?searchstring=" + this.props.location.search;
      fetch(requiredURL)
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  isLoaded: true,
                  posts: result.response.results
               });
               //console.log(this.state.posts)
            },

            // Handle error 
            (error) => {
               console.log(error);
               this.setState({
                  isLoaded: true,
                  error
               })
            },
         )
   }

   render() {
      return (
         <>
            <p>
               <strong>Location Props: </strong>
               {JSON.stringify(this.props.location, null, 2)}
            </p>
            <p>
               <strong>Query Param: </strong>
               {this.props.location.search}
            </p>
         </>
      );
   }
}

export default SearchPageTest;
