import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//React Share
import ShareIcon from '@material-ui/icons/Share';
import {
   EmailShareButton,
   FacebookShareButton,
   TwitterShareButton,
   FacebookIcon,
   EmailIcon,
   TwitterIcon
} from "react-share";
import { Modal, Button } from 'react-bootstrap';

//Loader Spinner
import { css } from "@emotion/core";
import BounceLoader from "react-spinners/BounceLoader";

class ExpandedCard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         isLoaded: false,
         shareTitle: '',
         shareURL: '',
         cardExpanded: false,
         expandedCardContent: {},
         errorExpanded: null
      };
      this.props = {
         currentCardId: '',
      }
   }

   truncateDate(dateinput) {
      return dateinput.substring(0, 10);
   }

   categoryHandler(category) {
      return category.toUpperCase();
   }


   imageHandler(url) {
      let currentimage = '';
      try {
         currentimage = url.elements[0].assets[0].file;
      } catch (e) {
         currentimage = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
      }
      return currentimage;
   }

   expandedCardContent() {

      const { errorExpanded, isLoaded, expandedCardContent } = this.state;

      if (errorExpanded) {
         return <div>Error in loading...Please try again by clicking the same link...:)</div>
      } /* else if (!isLoaded) {
         //Handling when page not loaded
         return <div style={{ textAlign: 'center', paddingTop: '100px' }}>
            <BounceLoader
               css={bounceCss}
               size={35}
               color={"#4A68E2"}
               loading={!isLoaded}
            />
            Loading
         </div>
      } */ else {
         return (
            <>
               <div>HELLO WORLD!</div>
               < img class="img-thumbnail" src={this.imageHandler(expandedCardContent.blocks.main)} style={{ width: '200px', minWidth: '200px', margin: '10px' }
               } alt="Image Not Found" />
               <div>
                  <p style={{ fontSize: '20px' }}><i>
                     <b >{expandedCardContent.webTitle}</b>
                     <ShareIcon onClick={() => {
                        this.toggleModal();
                        this.setState({
                           shareTitle: expandedCardContent.webTitle,
                           shareURL: expandedCardContent.webUrl
                        });
                     }} />
                  </i></p> {/* Title */}
                  <p style={{ fontSize: '12px' }}>
                     <Truncate lines={6} ellipsis={<span>... </span>}>
                        {expandedCardContent.blocks.body[0].bodyTextSummary}
                     </Truncate>
                  </p>
                  <div id='wrapper' >
                     <div id="left">{this.truncateDate(expandedCardContent.webPublicationDate)}</div>
                     <div id="right" style={this.categoryCss(expandedCardContent.sectionId)}>{this.categoryHandler(expandedCardContent.sectionId)}</div>
                  </div>
               </div>
            </>
         );
      }
   }

   componentDidMount() {
      //Fetching from the NodeJS backend
      let requiredURL = "http://localhost:9000/guardianAPIDetailed/" + this.state.cardId;
      fetch(requiredURL)
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  //isLoaded: true,
                  expandedCardContent: result.response.content
               });
               //console.log(this.state.posts)
            },

            // Handle error 
            (errorExpanded) => {
               console.log(errorExpanded);
               this.setState({
                  //isLoaded: true,
                  errorExpanded: true
               })
            },
         )
   }

   render() {
      const { error, isLoaded, posts } = this.state;

      if (error) {
         return <div>Error in loading...Please try again by clicking the same link...:)</div>
      } else if (!isLoaded) {
         //Handling when page not loaded
         return <div style={{ textAlign: 'center', paddingTop: '100px' }}>
            <BounceLoader
               css={bounceCss}
               size={35}
               color={"#4A68E2"}
               loading={!this.state.isLoaded}
            />
            Loading
         </div>
      } else {
         return (
            <div>
               {/* <ol>
                  {
                     posts.map(post => (
                        <li align="start">
                           <div>
                              <p>{post.title}</p>
                              <p>{post.id}</p>
                           </div>
                        </li>
                     ))
                  }
               </ol> */}

               <ExpandedCardDiv className={this.state.cardExpanded ? 'block' : 'hidden'}>
                  {this.expandedCardContent}
               </ExpandedCardDiv>

               <Modal size="lg" aria-labelledby="example-modal-sizes-title-lg" show={this.state.isShareModalOpen} onHide={this.toggleModal}>
                  <Modal.Header closeButton>
                     <Modal.Title id="example-modal-sizes-title-lg" >{this.state.shareTitle}</Modal.Title>
                  </Modal.Header>
                  <Modal.Body>
                     <div style={{ textAlign: 'center', fontSize: '25px' }}><b>Share via</b><br /></div>
                     <div style={{ textAlign: 'center', display: 'flex', justifyContent: 'space-around' }}>
                        <FacebookShareButton url={this.state.shareURL} hashtag='#CSCI_571_NewsApp' > <FacebookIcon size={64} round={true} /></FacebookShareButton>
                        <TwitterShareButton url={this.state.shareURL} title='#CSCI_571_NewsApp'><TwitterIcon size={64} round={true} /></TwitterShareButton>
                        <EmailShareButton url={this.state.shareURL} subject='#CSCI_571_NewsApp'><EmailIcon size={64} round={true} /></EmailShareButton>
                     </div>
                  </Modal.Body>
               </Modal>
            </div>
         );
      }
   }
}

const ExpandedCardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  display: flex;
  height: 720px;
  margin: 1.5rem;
  position: relative;
  width: 98%;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
   transition: all 0.3s cubic - bezier(.25, .8, .25, 1);
   padding - top: 5px;
  &: hover {
      /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
      box - shadow: 2.5px 5px 10px rgba(0, 0, 0, 0.19), 0 3px 3px rgba(0, 0, 0, 0.23);
      transform: translateY(-0.5px);
   },
   `;

const bounceCss = css`
   display: block;
   margin: 0 auto;
   border - color: red;
   `;

export default ExpandedCard;
