import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';


class HomePageGuardian extends Component {
   constructor(props) {
      super(props);
      this.state = {
         error: null,
         isLoaded: false,
         posts: [],
         categoryStyle: null
      };
   }

   truncateDate(dateinput) {
      return dateinput.substring(0, 10);
   }

   categoryHandler(category) {
      category = (category || '').replace(/^\s+|\s+$/g, '');
      category = category.toUpperCase();
      if (category === 'POLITICS') {
         this.state.categoryStyle = { backgroundColor: '#0893a1', color: '#fff' };
      }
      else if (category === 'WORLD') {
         this.state.categoryStyle = { backgroundColor: '#5904f6', color: '#fff' };
      }
      else if (category === 'SPORTS') {
         this.state.categoryStyle = { backgroundColor: '#fcb900', color: '#000' };
      }
      else if (category === 'BUSINESS') {
         this.state.categoryStyle = { backgroundColor: '#1273de', color: '#fff' };
      }
      else if (category === 'TECHNOLOGY') {
         this.state.categoryStyle = { backgroundColor: '#b80000', color: '#fff' };
      }
      else {
         this.state.categoryStyle = { backgroundColor: '#9b9b9b', color: '#fff' };
      }
      return category;
   }

   componentDidMount() {
      //Fetching from the NodeJS backend
      fetch("http://localhost:9000/guardianAPI")
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  isLoaded: true,
                  posts: result.response.results
               });
               console.log(this.state.posts)
            },

            // Handle error 
            (error) => {
               this.setState({
                  isLoaded: true,
                  error
               })
            },
         )
   }

   render() {
      const { error, isLoaded, posts } = this.state;

      if (error) {
         return <div>Error in loading</div>
      } else if (!isLoaded) {
         return <div>Loading ...</div>
      } else {
         return (
            <div>
               {/* <ol>
                  {
                     posts.map(post => (
                        <li align="start">
                           <div>
                              <p>{post.title}</p>
                              <p>{post.id}</p>
                           </div>
                        </li>
                     ))
                  }
               </ol> */}
               <ul style={{ listStyleType: "none", padding: "0" }}>
                  {
                     posts.map(post => (
                        <li >
                           <CardDiv>
                              {/*<img class="img-thumbnail" src={post.blocks.main.elements[0].assets[0].file} style={{ width: '200px', minWidth: '200px', margin: '10px' }} alt="Responsive image" />*/}
                              <img class="img-thumbnail" src="./assets/imagenotfound.png" style={{ width: '200px', minWidth: '200px', margin: '10px' }} alt="Image Not Found" />
                              <div>
                                 <p style={{ fontSize: '20px' }}><b><i>{post.webTitle}</i></b></p> {/* Title */}
                                 <p style={{ fontSize: '12px' }}>
                                    <Truncate lines={6} ellipsis={<span>... </span>}>
                                       {post.blocks.body[0].bodyTextSummary}
                                    </Truncate>
                                 </p>
                                 <div id='wrapper'>
                                    <div id="left">{this.truncateDate(post.webPublicationDate)}</div>
                                    <div id="right" style={this.categoryHandler(post.sectionId)}>{this.categoryHandler(post.sectionId)}</div>
                                 </div>
                              </div>
                           </CardDiv>
                        </li>
                     ))
                  }
               </ul>
            </div>
         );
      }
   }
}

const CardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  display: flex;
  height: 220px;
  margin: 1.5rem;
  position: relative;
  width: 98%;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
  transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  padding-top: 5px;
  &:hover {
        /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
        box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);
        transform: translateY(-0.5px);
    },
`;

export default HomePageGuardian;
