                    <Nav.Link to={'/search'}><Search /></Nav.Link>
                    <Form onSubmit={this.handleSubmit} style={{ paddingTop: '10px', paddingRight: '20px' }}>
                        <InputGroup className="input-group input-group-sm mb-3">
                            <input class="form-control form-control-sm" type="text" placeholder="Enter keyword..." />
                            <div class="input-group-append">
                                <span class="input-group-text" id="inputGroup-sizing-sm"><KeyboardArrowDownIcon style={{ fontSize: '15px' }} /></span>
                            </div>
                        </InputGroup>
                    </Form>
                    <Navbar.Collapse id="basic-navbar-nav">
                        <Nav className="mr-auto" activeKey="/home">
                            <Nav.Item><Nav.Link href="/home" to={'/'} style={navlink} activeClassName="selectedLink">Home</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/world" to={'/world'} style={navlink}>World</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/politics" to={'/politics'} style={navlink} >Politics</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/business" to={'/business'} style={navlink} >Business</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/technology" to={'/technology'} style={navlink} >Technology</Nav.Link></Nav.Item>
                            <Nav.Item><Nav.Link href="/sports" to={'/sports'} style={navlink} >Sports</Nav.Link></Nav.Item>

                            {/* <NavDropdown title="Dropdown" id="basic-nav-dropdown">
                            <NavDropdown.Item href="#action/3.1">Action</NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.2">Another action</NavDropdown.Item>
                            <NavDropdown.Item href="#action/3.3">Something</NavDropdown.Item>
                            <NavDropdown.Divider />
                            <NavDropdown.Item href="#action/3.4">Separated link</NavDropdown.Item>
                        </NavDropdown> */}

                        </Nav>

                        <>
                            {this.state.isThisBookmarksPage || this.state.isThisSearchPage
                                ?
                                <>
                                    <a data-tip="Bookmark" href='/bookmarks' to={'/bookmarks'} onClick={() => { this.setState({ isThisBookmarksPage: !this.state.isThisBookmarksPage }); localStorage.setItem('isThisBookmarksPage', !(localStorage.getItem('isThisBookmarksPage') === 'true')); }}>
                                        {this.state.isThisSearchPage ? <BookmarkBorderOutlinedIcon style={{ fontSize: 30, color: "#f9f9f9" }} /> : <BookmarkIcon style={{ fontSize: 30, color: "#fff" }} />}
                                    </a>
                                    <ReactTooltip place="bottom" type="dark" effect="float" />
                                </>
                                :
                                <>
                                    <a data-tip="Bookmark" href='/bookmarks' to={'/bookmarks'} onClick={() => { this.setState({ isThisBookmarksPage: !this.state.isThisBookmarksPage }); localStorage.setItem('isThisBookmarksPage', !(localStorage.getItem('isThisBookmarksPage') === 'true')); }}>
                                        <BookmarkBorderOutlinedIcon style={{ fontSize: 30, color: "#f9f9f9" }} />
                                    </a>
                                    <ReactTooltip place="bottom" type="dark" effect="float" />
                                    <div style={{ paddingLeft: '15px', paddingRight: '20px', color: "#f9f9f9" }}>NYTimes </div>
                                    <FormGroup>
                                        <FormControlLabel
                                            control={<BlueIOSSwitch checked={this.state.switch1} onChange={this.handleSwitchChange(1)} name="NYTorGuardian" />}
                                        />
                                    </FormGroup>
                                    <div style={{ color: "#f9f9f9" }}>Guardian </div>
                                </>
                            }
                        </>

                        {/*<a data-tip="Bookmark" href='/bookmarks' to={'/bookmarks'} onClick={() => { this.setState({ isThisBookmarksPage: !this.state.isThisBookmarksPage }); localStorage.setItem('isThisBookmarksPage', !(localStorage.getItem('isThisBookmarksPage') === 'true')); }}>
                            {this.state.isThisBookmarksPage
                                ? <BookmarkIcon style={{ fontSize: 30, color: "#fff" }} />
                                : <BookmarkBorderOutlinedIcon style={{ fontSize: 30, color: "#f9f9f9" }} />
                            }
                        </a>

                        <ReactTooltip place="bottom" type="dark" effect="float" />


                        <div style={{ paddingLeft: '15px', paddingRight: '20px', color: "#f9f9f9" }}>NYTimes </div>
                        <FormGroup>
                            <FormControlLabel
                                control={<BlueIOSSwitch checked={this.state.switch1} onChange={this.handleSwitchChange(1)} name="NYTorGuardian" />}
                            />
                        </FormGroup>
                        <div style={{ color: "#f9f9f9" }}>Guardian </div>*/}

                    </Navbar.Collapse>
                </Navbar >

                <div>
                    {this.state.switch1
                        ? <Switch>
                            {/*
                    <Route exact path='/' component={GuardianPages} />
                    <Route path='/world' component={GuardianPages} />
                    <Route path='/politics' component={GuardianPages} />
                    <Route path='/business' component={GuardianPages} />
                    <Route path='/technology' component={GuardianPages} />
                    <Route path='/sports' component={GuardianPages} />
                    */}
                            <Route exact path='/' component={() => <GuardianPages currentPage={'home'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/home' render={(props) => <GuardianPages {...props} currentPage={'home'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/world' component={() => <GuardianPages currentPage={'world'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/politics' render={(props) => <GuardianPages {...props} currentPage={'politics'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/business' render={(props) => <GuardianPages {...props} currentPage={'business'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/technology' render={(props) => <GuardianPages {...props} currentPage={'technology'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/sports' render={(props) => <GuardianPages {...props} currentPage={'sports'} searchOrBookmarksNavHandler={this.handlerToSetSearchAndBookmarksToFalseForNav} />} />
                            <Route path='/bookmarks' render={(props) => <BookmarksPage {...props} />} />
                            <Route path='/search' render={(props) => <SearchResultsPage {...props} searchQuery={''} currentOrigin={'GUARDIAN'} searchTrueHandler={this.handlerToSetSearchToTrueForNav} />} />
                        </Switch>
                        : <div></div>
                    }
                </div>


            </Router >
        );
    }
}

const navlink = {
    color: '#9b9b9b',
    "&:hover": {
        color: '#f9f9f9'
    },
    "&:active": {
        color: '#f9f9f9'
    }
};

