import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//React Share
import ShareIcon from '@material-ui/icons/Share';
import {
   EmailShareButton,
   FacebookShareButton,
   TwitterShareButton,
   FacebookIcon,
   EmailIcon,
   TwitterIcon
} from "react-share";
import { Modal, Button, Container } from 'react-bootstrap';

//Loader Spinner
import { css } from "@emotion/core";
import BounceLoader from "react-spinners/BounceLoader";

//Tooltip for hover over bookmarks
import ReactTooltip from 'react-tooltip';
import BookmarkBorderOutlinedIcon from '@material-ui/icons/BookmarkBorderOutlined';

//Expand More
import ExpandMoreIcon from '@material-ui/icons/ExpandMore';
import ExpandLessIcon from '@material-ui/icons/ExpandLess';

//Time in text format
import Moment from 'react-moment';

//Bookmarks 
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';
import BookmarkIcon from '@material-ui/icons/Bookmark';

//Comment Box
import CommentBox from './CommentBox';

//Media Queries
import { useMediaQuery } from 'react-responsive';
import MediaQuery from 'react-responsive';

class ExpandedCard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         isLoaded: false,
         shareTitle: '',
         shareURL: '',
         cardExpanded: false,
         expandedCardContent: {},
         errorExpanded: null,
         expandAdditionalContent: false,
      };
      this.props = {
         isPartOfBookmarksProp: false,
         currentCardId: ''
      }
      this.state.isPartOfBookmarks = this.props.isPartOfBookmarksProp;
   }

   truncateDate(dateinput) {
      return dateinput.substring(0, 10);
   }

   categoryHandler(category) {
      return category.toUpperCase();
   }


   imageHandler(url) {
      let currentimage = '';
      try {
         currentimage = url.elements[0].assets[url.elements[0].assets.length - 1].file;
      } catch (e) {
         currentimage = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
      }
      return currentimage;
   }

   toggleExpandAdditionalContent = () => {
      this.setState({
         expandAdditionalContent: !this.state.expandAdditionalContent
      });
   }

   notifyA = articleTitle => toast('Saving ' + articleTitle);

   bookmarkHandler(currentItem) {
      //Checking whether other bookmarks exist
      var retrievedObject = localStorage.getItem('bookmarks');

      if (retrievedObject == null) {
         // Put the object into storage
         let arrayOfBookmarks = [{ 'origin': 'GUARDIAN', 'datacontent': currentItem }]
         localStorage.setItem('bookmarks', JSON.stringify(arrayOfBookmarks));
      }
      else {
         //Append it to the list and put it back into storage
         let retrievedObjectNew = JSON.parse(retrievedObject)
         retrievedObjectNew.push({ 'origin': 'GUARDIAN', 'datacontent': currentItem });
         localStorage.setItem('bookmarks', JSON.stringify(retrievedObjectNew));
      }

   }

   componentDidMount() {
      //Fetching from the NodeJS backend
      let requiredURL = "http://localhost:9000/guardianAPIDetailed?id=" + this.props.currentCardId;
      fetch(requiredURL)
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  isLoaded: true,
                  expandedCardContent: result.response
               });
               //console.log(this.state.posts)
            },

            // Handle error 
            (errorExpanded) => {
               console.log(errorExpanded);
               this.setState({
                  isLoaded: true,
                  errorExpanded: true
               })
            },
         )
   }

   render() {
      const { errorExpanded, isLoaded, expandedCardContent } = this.state;

      if (errorExpanded) {
         return <div>Error in loading...Please try again by clicking the same link...:)</div>
      } else if (!isLoaded) {
         //Handling when page not loaded
         return <div style={{ textAlign: 'center', paddingTop: '100px' }}>
            <BounceLoader
               css={bounceCss}
               size={35}
               color={"#4A68E2"}
               loading={!isLoaded}
            />
            Loading
         </div>
      } else {
         return (
            <div>
               <Container style={{ maxWidth: '100%' }}>
                  <ExpandedCardDiv>

                     <div>
                        <MediaQuery minDeviceWidth={1224} device={{ deviceWidth: 1600 }}>

                        </MediaQuery>
                        <Desktop>
                           <p style={{ fontSize: '3.5vw', paddingLeft: '30px', paddingTop: '20px', paddingRight: '10px' }}><i> <b >{expandedCardContent.content.webTitle}</b></i></p>
                        </Desktop>
                        <Tablet>Tablet</Tablet>
                        <Mobile>Mobile</Mobile>
                        <Default>Not mobile (desktop or laptop or tablet)</Default>
                        <div>
                           <div id='wrapper'>
                              <div id="leftexpanded" style={{ paddingLeft: '30px', paddingRight: '30px', fontSize: '20px' }}>
                                 <Moment format="DD MMMM YYYY">{this.truncateDate(expandedCardContent.content.webPublicationDate)}</Moment>
                              </div>
                              <div id="rightexpanded">
                                 <div style={{ paddingLeft: '10px' }}>
                                    <FacebookShareButton url={expandedCardContent.content.webUrl} hashtag='#CSCI_571_NewsApp' > <FacebookIcon size={34} round={true} /></FacebookShareButton>
                                    <TwitterShareButton url={expandedCardContent.content.webUrl} title='#CSCI_571_NewsApp'><TwitterIcon size={34} round={true} /></TwitterShareButton>
                                    <EmailShareButton style={{ paddingRight: '50px' }} url={expandedCardContent.content.webUrl} subject='#CSCI_571_NewsApp'><EmailIcon size={34} round={true} /></EmailShareButton>

                                    <a data-tip="Bookmark" data-for='expandedBookmarks' onClick={() => { this.notifyA(expandedCardContent.content.webTitle); this.bookmarkHandler(expandedCardContent.content); this.setState({ isPartOfBookmarks: !this.state.isPartOfBookmarks }); }}>
                                       {this.state.isPartOfBookmarks
                                          ? <BookmarkIcon style={{ fontSize: 30, color: "#b80000" }} />
                                          : <BookmarkBorderOutlinedIcon style={{ fontSize: 30, color: "#b80000" }} />
                                       }
                                    </a>
                                    <ReactTooltip id='expandedBookmarks' place="top" type="dark" effect="float" />

                                 </div>
                              </div>
                           </div>
                        </div>


                        <div>< img class="img-thumbnail" src={this.imageHandler(expandedCardContent.content.blocks.main)} alt="Image Is Loading..." /></div>

                        <p style={{ fontSize: '12px', paddingTop: '20px' }}>
                           {!this.state.expandAdditionalContent &&
                              <div>
                                 <Truncate lines={4} ellipsis={<span>... <br /><div style={{ alignContent: 'right' }}>
                                    <div style={{ textAlign: 'right', paddingRight: '20px' }}>
                                       <ExpandMoreIcon style={{ fontSize: '40px' }} onClick={e => this.toggleExpandAdditionalContent()} />
                                    </div>
                                 </div></span>}>
                                    {expandedCardContent.content.blocks.body[0].bodyTextSummary}
                                 </Truncate>
                              </div>
                           }
                           {
                              this.state.expandAdditionalContent &&
                              <div>
                                 {expandedCardContent.content.blocks.body[0].bodyTextSummary}
                                 <div style={{ alignContent: 'right' }}>
                                    <div style={{ textAlign: 'right', paddingRight: '20px' }}>
                                       <ExpandLessIcon style={{ fontSize: '40px' }} onClick={e => this.toggleExpandAdditionalContent()} />
                                    </div>
                                 </div>
                              </div>
                           }
                        </p>
                     </div>
                  </ExpandedCardDiv>
               </Container>
               <CommentBox currentCardId={this.props.currentCardId} />
            </div>
         );
      }
   }
}

