import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//React Share
import ShareIcon from '@material-ui/icons/Share';
import {
   EmailShareButton,
   FacebookShareButton,
   TwitterShareButton,
   FacebookIcon,
   EmailIcon,
   TwitterIcon
} from "react-share";
import { Modal, Button } from 'react-bootstrap';

//Loader Spinner
import { css } from "@emotion/core";
import BounceLoader from "react-spinners/BounceLoader";

//Expanded Card Div
import ExpandedCard from './ExpandedCard';

class SearchResultsPage extends Component {
   constructor(props) {
      super(props);
      this.state = {
         posts: [],
         isShareModalOpen: false,
         shareTitle: '',
         shareOrigin: '',
         shareURL: '',
         cardExpanded: false,
         cardIdentifierentifier: '',
         expandedCardContent: {},
      };
      this.props = {
         currentPage: '',
         currentOrigin: '',
      }

   }

   truncateDate(dateinput) {
      return dateinput.substring(0, 10);
   }

   categoryHandler(category) {
      return category.toUpperCase();
   }

   categoryCss(category) {
      category = (category || '').replace(/^\s+|\s+$/g, '');
      category = category.toUpperCase();
      if (category === 'POLITICS') {
         return { backgroundColor: '#0893a1', color: '#fff', float: 'left', paddingLeft: '2px', paddingRight: '2px', borderRadius: '2px' };
      }
      else if (category === 'WORLD') {
         return { backgroundColor: '#5904f6', color: '#fff', float: 'left', paddingLeft: '2px', paddingRight: '2px', borderRadius: '2px' };
      }
      else if (category === 'SPORT') {
         return { backgroundColor: '#fcb900', color: '#000', float: 'left', paddingLeft: '2px', paddingRight: '2px', borderRadius: '2px' };
      }
      else if (category === 'BUSINESS') {
         return { backgroundColor: '#1273de', color: '#fff', float: 'left', paddingLeft: '2px', paddingRight: '2px', borderRadius: '2px' };
      }
      else if (category === 'TECHNOLOGY') {
         return { backgroundColor: '#8bc34a', color: '#000', float: 'left', paddingLeft: '2px', paddingRight: '2px', borderRadius: '2px' };
      }
      else {
         return { backgroundColor: '#9b9b9b', color: '#fff', float: 'left', paddingLeft: '2px', paddingRight: '2px', borderRadius: '2px' };
      }
   }

   imageHandler(url) {
      let currentimage = '';
      try {
         currentimage = url.elements[0].assets[1].file;
      } catch (e) {
         currentimage = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
      }
      return currentimage;
   }

   toggleModal = () => {
      this.setState({
         isShareModalOpen: !this.state.isShareModalOpen
      });
   }

   doExpand = id => {
      this.setState({
         cardIdentifier: id,
         cardExpanded: true,
      });
   }

   componentDidMount() {
      //Setting Search to true
      this.props.searchTrueHandler();

      //Fetching from the NodeJS backend
      let requiredURL = '';
      requiredURL = "http://localhost:9000/guardianAPISearch?searchstring=" + this.props.location.search;
      fetch(requiredURL)
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  isLoaded: true,
                  posts: result.response.results
               });
               //console.log(this.state.posts)
            },

            // Handle error 
            (error) => {
               console.log(error);
               this.setState({
                  isLoaded: true,
                  error
               })
            },
         )
   }

   render() {
      return (
         <>
            <p>
               <strong>Location Props: </strong>
               {JSON.stringify(this.props.location, null, 2)}
            </p>
            <p>
               <strong>Query Param: </strong>
               {this.props.location.search}
            </p>
            <p>
               <strong>Post data: </strong>
               {JSON.stringify(this.state.posts, null, 2)}
            </p>
         </>
      );
   }
}

const CardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  height: 320px;
  margin: 1.5rem;
  width: 300px;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
  transition: all 0.3s cubic-bezier(.25,.8,.25,1);
  padding-top: 5px;
  &:hover {
        /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
        box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);
        transform: translateY(-0.5px);
    },
`;

const bounceCss = css`
  display: block;
  margin: 0 auto;
  border-color: red;
`;

export default SearchPageTest;
