import React, { Component } from 'react';

//Cards
import styled from 'styled-components'

//Truncating Content in Cards
import Truncate from 'react-truncate';

//React Share
import ShareIcon from '@material-ui/icons/Share';
import {
   EmailShareButton,
   FacebookShareButton,
   TwitterShareButton,
   FacebookIcon,
   EmailIcon,
   TwitterIcon
} from "react-share";
import { Modal, Button } from 'react-bootstrap';

//Loader Spinner
import { css } from "@emotion/core";
import BounceLoader from "react-spinners/BounceLoader";

//Tooltip for hover over bookmarks
import ReactTooltip from 'react-tooltip';
import BookmarkBorderOutlinedIcon from '@material-ui/icons/BookmarkBorderOutlined';


class ExpandedCard extends Component {
   constructor(props) {
      super(props);
      this.state = {
         isLoaded: false,
         shareTitle: '',
         shareURL: '',
         cardExpanded: false,
         expandedCardContent: {},
         errorExpanded: null
      };
      this.props = {
         currentCardId: '',
      }
   }

   truncateDate(dateinput) {
      return dateinput.substring(0, 10);
   }

   categoryHandler(category) {
      return category.toUpperCase();
   }


   imageHandler(url) {
      let currentimage = '';
      try {
         currentimage = url.elements[0].assets[url.elements[0].assets.length - 1].file;
      } catch (e) {
         currentimage = 'https://assets.guim.co.uk/images/eada8aa27c12fe2d5afa3a89d3fbae0d/fallback-logo.png';
      }
      return currentimage;
   }

   componentDidMount() {
      //Fetching from the NodeJS backend
      let requiredURL = "http://localhost:9000/guardianAPIDetailed?id=" + this.props.currentCardId;
      fetch(requiredURL)
         .then(response => response.json())
         .then(
            // handle the result
            (result) => {
               this.setState({
                  isLoaded: true,
                  expandedCardContent: result.response
               });
               console.log(this.state.posts)
            },

            // Handle error 
            (errorExpanded) => {
               console.log(errorExpanded);
               this.setState({
                  isLoaded: true,
                  errorExpanded: true
               })
            },
         )
   }

   render() {
      const { errorExpanded, isLoaded, expandedCardContent } = this.state;

      if (errorExpanded) {
         return <div>Error in loading...Please try again by clicking the same link...:)</div>
      } else if (!isLoaded) {
         //Handling when page not loaded
         return <div style={{ textAlign: 'center', paddingTop: '100px' }}>
            <BounceLoader
               css={bounceCss}
               size={35}
               color={"#4A68E2"}
               loading={!isLoaded}
            />
            Loading
         </div>
      } else {
         return (
            <ExpandedCardDiv>

               <div>
                  <p style={{ fontSize: '40px', paddingLeft: '30px', paddingTop: '20px', paddingRight: '10px' }}><i> <b >{expandedCardContent.content.webTitle}</b>                  </i></p> {/* Title */}
                  <div>
                     <div id='wrapper'>
                        <div id="leftexpanded" style={{ paddingLeft: '30px', paddingRight: '30px', fontSize: '20px' }}>{this.truncateDate(expandedCardContent.content.webPublicationDate)}</div>
                        <div id="rightexpanded">
                           <div style={{ paddingLeft: '10px' }}>
                              <FacebookShareButton url={this.state.shareURL} hashtag='#CSCI_571_NewsApp' > <FacebookIcon size={34} round={true} /></FacebookShareButton>
                              <TwitterShareButton url={this.state.shareURL} title='#CSCI_571_NewsApp'><TwitterIcon size={34} round={true} /></TwitterShareButton>
                              <EmailShareButton style={{ paddingRight: '50px' }} url={this.state.shareURL} subject='#CSCI_571_NewsApp'><EmailIcon size={34} round={true} /></EmailShareButton>

                              <a data-tip="Bookmark"> <BookmarkBorderOutlinedIcon style={{ fontSize: 30, color: "#000" }} /> </a>
                              <ReactTooltip place="top" type="dark" effect="float" />

                           </div>
                        </div>
                     </div>
                  </div>


                  < img class="img-thumbnail" src={this.imageHandler(expandedCardContent.content.blocks.main)} style={{
                     width: '95%', margin: '10%'
                  }
                  } alt="Image Not Found" />

                  <p style={{ fontSize: '12px' }}>
                     <Truncate lines={6} ellipsis={<span>... </span>}>
                        {expandedCardContent.content.blocks.body[0].bodyTextSummary}
                     </Truncate>
                  </p>
               </div>
            </ExpandedCardDiv>
         );
      }
   }
}

const ExpandedCardDiv = styled.div`
  background: #fff;
  border-radius: 2px;
  display: flex;
  height: 100%;
  margin: 1.5rem;
  position: relative;
  width: 98%;
  box-shadow: 0.2px 1px 3px rgba(0,0,0,0.12), 0 1px 2px rgba(0,0,0,0.24);
  /*box-shadow: 2.5px 5px 10px rgba(0,0,0,0.19), 0 3px 3px rgba(0,0,0,0.23);*/
   transition: all 0.3s cubic - bezier(.25, .8, .25, 1);
   padding - top: 5px;
  &: hover {
      /*box-shadow: 0 14px 28px rgba(0,0,0,0.25), 0 10px 10px rgba(0,0,0,0.22);*/
      box - shadow: 2.5px 5px 10px rgba(0, 0, 0, 0.19), 0 3px 3px rgba(0, 0, 0, 0.23);
      transform: translateY(-0.5px);
   },
   `;

const bounceCss = css`
   display: block;
   margin: 0 auto;
   border - color: red;
   `;

export default ExpandedCard;
