import React, { Component } from 'react'
import axios from 'axios'

import { Button, Navbar, Nav, NavLink, NavDropdown, Form, FormControl } from 'react-bootstrap'
import InputGroup from 'react-bootstrap/InputGroup'
import KeyboardArrowDownIcon from '@material-ui/icons/KeyboardArrowDown';

import Suggestions from './Suggestions'

class Search extends React.Component {
   constructor(props) {
      super(props);
      this.state = {
         name: null,
      };
   }

   handleChange = (e) => {
      this.setState({
         [e.target.name]: e.target.value
      })
   }

   onSubmit = (e) => {
      e.preventDefault();
      const form = {
         name: this.state.name,
      }
      {/* -----------you would send data to API to get results, I used database for ease, this also clears the form on submit----------------*/ }
      this.props.searchQueryProp(this.state.name);
      this.setState({
         name: null,
      })
   }

   render() {
      return (
         <div>
            <Form>
               <InputGroup className="input-group input-group-sm mb-3">
                  <input
                     class="form-control form-control-sm"
                     name='name'
                     value={this.state.name}
                     onChange={e => this.handleChange(e)}
                     placeholder="Enter keyword..."
                  />

                  <div class="input-group-append">
                     <span class="input-group-text" id="inputGroup-sizing-sm"><KeyboardArrowDownIcon style={{ fontSize: '15px' }} /></span>
                  </div>
                  <button onClick={(e) => this.onSubmit(e)} style={{ hidden: 'none' }}>SUBMIT</button>
               </InputGroup>
            </Form>
         </div>
      );
   }
}

export default Search;
